import React, { useEffect, useRef } from "react";
import { Sprite, Container, Text } from '@inlet/react-pixi';
import { gsap } from "gsap";
import { TweenLite } from "gsap/src";
import { TweenMax } from "gsap/gsap-core";




export default function UnitOpened({ items, cur, headerStyle, ref }) {

    const prilipalaRef = useRef()


    useEffect(() => {
        TweenMax.to(prilipalaRef.current,
            {
                x: "170",
                rotation: '125.3',
                // opacity: 0,
                scale: "1.5",


            }
        //    { css: { scale: .05, opacity: 0, rotation: 180 }}
        );
    }, [])

    return (
        <Container x={370} y={20} width={600} height={530}>
            <Sprite image={items[cur - 1].topEgg} width={186} height={134} x={450} y={80} anchor={.5} />
            <Sprite image={items[cur - 1].bottomEgg} width={186} height={127} x={450} y={300} anchor={.5} />
            <Sprite image={items[cur - 1].prilipala} width={173} height={241} x={360} y={200} anchor={.5} ref={prilipalaRef} />
            <Text text={items[cur - 1].name} style={headerStyle} x={50} y={450} />
        </Container>
    )
}
