import React, { useState } from "react";
import InputMask from "react-input-mask";
import API from "../../utils/API";
import arr from '../../assets/img/btn-arr.png'
import { useDispatch } from 'react-redux';
import Countdown from 'react-countdown';
import $ from 'jquery'



export default function LoginForm() {
    const [timer, setTimer] = useState(false)
    const [code, setCode] = useState()
    const [phoneNumber, setPhone] = useState()
    const [error, setErrorMessage] = useState()

    const renderer = ({ seconds, completed }) => {
        if (completed) {
            return <Completionist />;
        }

        else {
            return (
                <p className="formCodeAgain" >
                    {timer === 'ended' ?
                        <>Код отправлен повторно</> :
                        <div className='mt-4 mx-auto text-center'>
                            Повторная отправка возможна через{' '}
                            <span className="text-violet">{seconds}</span> сек
                        </div>
                    }
                </p >)


        }
    };
    const Completionist = () => (
        <a className="btnLink orange"
            onClick={() => stopTimer()}
        >Отправить повторно</a>
    );
    const startTimer = (e) => {
        setTimer(true)
        setPhone(null)
        $('#user_phone').val('')
    }

    // Код отправлен повторно
    // const  stopTimer = () => {
    //     this.setState({
    //         timer: 'ended'
    //     })
    //     const { handleSendCode } = this.props
    //     handleSendCode(this.state.number)
    //     $('.modalBox__container').trigger('click')
    // }

    const getCode = (e) => {
        e.preventDefault()
        API.post('/login_assms', {
            phone: phoneNumber
        }).then(res => {
            console.log(res.data)
            if (res.data.success) {
                startTimer()
            }
            else{
                setErrorMessage(res.data.message)
            }
        })
        .catch(error => {
            setErrorMessage(error.message)
        })
    }
    const login = async () => {
        await API.post('/sms')
            .then(res => console.log(res.data))
    }
    const setCorrectPhone = (e) => {
        setErrorMessage(false)
        let cur = e.target.value
        cur = cur.replaceAll(/\s/g, '').replaceAll(/[{()}]/g, '').replaceAll('+', '')
        setPhone(cur)
    }

    return (
        <div>
            <h2 className='text-violet text-center text-3xl mb-8'>
                вход/регистрация </h2>
            <form
                onSubmit={
                    (e) => {
                        e.preventDefault();
                        !code ? getCode(e) : login(e)
                    }
                }
            >
              {error ?  <p className="text-xxs text-red text-center mb-4">{error}</p> : ''}
                <InputMask
                    className={`w-full ${error ? 'error':''}`}
                    placeholder={timer ? 'Введите код' : 'Введите номер телефона'}
                    mask={timer ? '999999' : "+7 (999) 999 99 99"}
                    required
                    autofill='false'
                    id="user_phone"
                    value={timer ? code : phoneNumber}
                    onChange={(e) => { timer ? setCode(e.target.value) : setCorrectPhone(e) }}
                    maskChar="" />
                {timer ?
                    <Countdown date={Date.now() + 59000} intervalDelay={1000} precision={.3} renderer={renderer}>
                        <Completionist />
                    </Countdown> : null}
                <button className='btn grid py-4 px-8 w-fit mx-auto mt-8' type='submit'>
                    <span>отправить код</span>
                    <img src={arr} alt="" />
                </button>
            </form>
        </div >
    )
}
