import React, { useEffect, useState } from "react";
import Main from "./components/Main";
import Mechanics from "./components/Mechanics";
import Nav from "./components/Nav";
import Participate from "./components/Participate";
import logo1 from './assets/img/footer/logo1.png'
import logo2 from './assets/img/footer/logo2.png'
import logo3 from './assets/img/footer/logo3.png'
import FormComponent from "./components/FormComponent";
import Units from "./components/Units";

import { Hoc } from "./Hoc";
import { startStars } from "./constants/stars";
import Winners from "./components/Winners";
import API from "./utils/API";
import Serial from "./components/Serial";
import Application from "./components/Application";
import Sponsors from './components/Sponsors'


function IndexPage(props) {

  const [shop, selectShop] = useState('dixy')
  const [winnersMas, setWinners] = useState(false)
  const {
    setForm,
    openScaner,
    setScanerForm } = props
  const {
    codeVerified,
    form,
    scaner,
    scanerForm,
    winners
  } = props.state.store


  useEffect(() => {
    if (particlesJS) { startStars() }
  }, [])
  useEffect(() => {
    API.get('/weekly_raffle_winners').then(
      res => {
        setWinners(res.data)
      }
    )
  }, [])
  return (
    <div className="content">

      {shop ?
        <div className='content-inner'>
          <div id="particle-container"></div>
          <Nav setForm={setForm} openScaner={openScaner} />
          <Main />
          <Mechanics />
          <Units />
          <Participate />
          <Serial />
          <Application />
          <Sponsors />
          {winnersMas ? <Winners winners={winnersMas} /> : null}
        </div>
        :
        <div className='grid place-items-center p-16'>
          <h4 className='mb-8 text text-2xl text-orange'>Выбери магазин</h4>
          <div className="flex justify-center gap-2">
            <div className="btn px-8 py-4 text-white pointer" onClick={() => selectShop('dixy')}>Дикси</div>
            <div className="btn px-8 py-4 text-white pointer" onClick={() => selectShop('megamart')}>Мегамарт</div>
          </div>
        </div>
      }
      {form ?
        <FormComponent
          setForm={setForm}
          scaner={scaner}
          openScaner={openScaner}
          scanerForm={scanerForm}
          setScanerForm={setScanerForm}
          form={form}
          codeVerified={codeVerified} />
        : <></>}
      <footer className='bg-violet py-8'>
        <div className="container mx-auto">
          <p className='text text-white '>Совершите покупку в любом магазине «ДИКСИ» на сумму от 600 руб.* либо купите товары-спонсоры**, участвующие в акции, и получите на кассе игрушку  в подарок. Общий срок проведения акции: 22.11.2021 - 19.12.2021; *600 руб. без учета табачной продукции (табака, табачных изделий и курительных принадлежностей, в том числе трубок, кальянов, сигаретной бумаги, зажигалок, никотинсодержащей продукции и устройств для ее потребления) и алкогольной продукции. Сумма покупки рассчитывается после применения всех скидок.</p>
          <p className='text text-white mt-4'>** Полный список товаров-спонсоров и условия (количество) их приобретения размещен на сайте tresnutye2.dixy.ru. Список товаров-спонсоров может меняться в течение всего срока проведения акции. Организатор акции оставляет за собой право изменить срок и условия акции. В период проведения акции не гарантируется наличие игрушек в каждом магазине. В случае, если запас игрушек в месте его продажи подошел к концу, акция в отношении данного товара прекращается досрочно. Акционные товары могут отличаться по внешнему виду и техническим характеристикам от изображений в рекламе. Подробности проведения акции, информацию об организаторе акции, правила ее проведения, сроки, адреса, порядок получения игрушек можете узнать на сайте tresnutye2.dixy.ru Количество игрушек ограничено.</p>
          <div className="flex mt-8 gap-8 items-center justify-center">
            <div>
              <img src={logo1} alt="" />
            </div>
            <div>
              <img src={logo2} alt="" />
            </div>
            <div>
              <img src={logo3} alt="" />
            </div>
          </div>
        </div>
      </footer>
    </div>
  );

}

export default Hoc(IndexPage);
