import img1 from '../../assets/img/units/zemlyaka/top.png'
import img2 from '../../assets/img/units/zemlyaka/bottom.png'
import img3 from '../../assets/img/units/zemlyaka/prilipala.png'
import img4 from '../../assets/img/units/zemlyaka/zemlyaka.png'
import img5 from '../../assets/img/units/Mercusha/mercusha.png'
import img6 from '../../assets/img/units/sinishain/sinishain.png'
import bg from '../../assets/img/violet-units.png'
import bg2 from '../../assets/img/white-units.png'
import arr_up from '../../assets/img/arr-units-up.png'
import arr_down from '../../assets/img/arr-units-down.png'
import React, { useState, useRef } from 'react';
import { Stage, Sprite, Container, Text } from '@inlet/react-pixi';
import UnitEgg from './Unitegg'
import * as PIXI from 'pixi.js'
import { gsap } from "gsap";


const items = [
    {
        id: 1,
        tresnutyi: img4,
        prilipala: img3,
        topEgg: img1,
        bottomEgg: img2,
        name: 'Земляка',
        description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua'
    },
    {
        id: 2,
        tresnutyi: img6,
        prilipala: img3,
        topEgg: img1,
        bottomEgg: img2,
        name: 'Синнишайн',
        description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua'
    },
    {
        id: 3,
        tresnutyi: img5,
        prilipala: img3,
        topEgg: img1,
        bottomEgg: img2,
        name: 'Меркуша',
        description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua'
    },
    {
        id: 4,
        tresnutyi: img4,
        prilipala: img3,
        topEgg: img1,
        bottomEgg: img2,
        name: 'Земляка4',
        description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua'
    },
    {
        id: 5,
        tresnutyi: img4,
        prilipala: img3,
        topEgg: img1,
        bottomEgg: img2,
        name: 'Земляка5',
        description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua'
    },
    {
        id: 6,
        tresnutyi: img4,
        prilipala: img3,
        topEgg: img1,
        bottomEgg: img2,
        name: 'Земляка6',
        description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua'
    }
]
const style = new PIXI.TextStyle({
    align: "center",
    fontFamily: "Junegull",
    fontSize: 18,
    fontWeight: "bold",
    fill: ["#fff"],
});

const headerStyle = new PIXI.TextStyle({
    align: "center",
    fontFamily: "Junegull",
    fontSize: 42,
    fontWeight: "bold",
    fill: ["#7400BB"]
});


export default function UnitsBlock() {
    const [index, setIndex] = useState(0)
    const [active, setActive] = useState(0)
    const [cur, setCur] = useState(1)
    const eggRef = useRef()

    const showNext = () => {
        if (index + 1 > items.length - 3) {
            setIndex(0)
        } else {
            setIndex(index + 1)
        }

    }
    const showPrev = () => {
        if (index - 3 < 0) {
            setIndex(items.length - 3)
        } else {
            setIndex(index - 1)
        }
    }

    return (
        <Stage width={1100} height={700} options={{ backgroundAlpha: 0 }}>
            <Sprite width={942} height={629} image={bg} x={0} y={30} />
            <Sprite image={bg2} x={335} y={0} width={678} height={619} />
            <Container>
                {items.slice(index, index + 3).map(
                    (egg, id) => (
                        <UnitEgg
                            egg={egg}
                            key={id}
                            x={120}
                            y={id === 1 ? 276 : id === 2 ? 410 : 140}
                            setActive={() => { setActive(id); setCur(egg.id) }}
                            active={id === active ? true : false}
ref={egg}
                        />
                    )
                )}
            </Container>
            <Container x={80} y={500}>
                <Sprite image={arr_up}
                    interactive={true}
                    buttonMode={true}
                    pointerdown={showPrev}
                />
                <Sprite image={arr_down}
                    interactive={true}
                    buttonMode={true}
                    x={80}
                    pointerdown={showNext}
                />
                <Text text={`${cur} / ${items.length}`} style={style} x={160} y={30} />
            </Container>

            <Container x={370} y={20} width={600} height={530}>
                <Sprite image={items[cur - 1].topEgg} width={186} height={134} x={450} y={80} anchor={.5} />
                <Sprite image={items[cur - 1].bottomEgg} width={186} height={127} x={450} y={300} anchor={.5} />
                <Sprite image={items[cur-1].prilipala} width={173} height={241} x={170} y={200} anchor={.5} />
                <Text text={items[cur - 1].name} style={headerStyle} x={50} y={450} />
            </Container>

        </Stage>
    )
}
