import React, { useState } from "react";
import Application from "./components/Application";
import Main from "./components/Main";
import Mechanics from "./components/Mechanics";
import Nav from "./components/Nav";
import Participate from "./components/Participate";
import Prizes from "./components/Prizes";
import Sponsors from "./components/Sponsors";
import Units from "./components/Units";

import { Hoc } from "./Hoc";
// WinnersComponent

function IndexPage() {

  const [shop, selectShop] = useState('dixy')
  console.log(shop)
  return (
    <div className="content">
      {shop ?
        <div className='content-inner'>
          <Nav />
          <Main />
          <Mechanics />
          <Units />
          <Participate />

          <Sponsors />
        </div> :
        <div className='grid place-items-center p-16'>
          <h4 className='mb-8 text text-2xl text-orange'>Выбери магазин</h4>
          <div className="flex justify-center gap-2">
            <div className="btn px-8 py-4 text-white pointer" onClick={() => selectShop('dixy')}>Дикси</div>
            <div className="btn px-8 py-4 text-white pointer" onClick={() => selectShop('megamart')}>Мегамарт</div>
          </div>
        </div>
      }
    </div>
  );

}

export default Hoc(IndexPage);
