import React, { useState } from "react";
import LoginForm from "./LoginForm";
import UserAccount from "./UserAccount";
import UserForm from "./UserForm";



export default function FormComponent({ setform }) {
  const [form, setForm] = useState(true)
  const [token, setToken] = useState(window.localStorage.getItem('tresnutye2021-token'))

  const close = () => {
    document.querySelector("#root").classList.remove("scrolled");
    setform(false)
  }
  return (
    <div className={form ? "modalBox " : "modalBox hidden"}>
      <div
        className={
          token
            ? "modalBox__container"
            : "modalBox__container phone"
        }
      >
        <div className="close-btn__container cursor-pointer"
        // onClick={close}
        >
          <div className="close-btn">
            <span></span>
            <span></span>
          </div>
        </div>
        <div className="modalBox__container__content">
          {token ?
            <UserForm setForm={setForm} /> :
            <LoginForm setToken={this.setToken} />}
        </div>
      </div>
    </div>
  )
}
