import bg from '../../assets/img/violet-units.png'
import bg_m from '../../assets/img/violet-units-mob.png'
import bg2 from '../../assets/img/white-units.png'
import bg2_m from '../../assets/img/white-units-mob.png'
import arr_up from '../../assets/img/arr-units-up.png'
import arr_down from '../../assets/img/arr-units-down.png'
import React, { useState, useRef } from 'react';
import { Stage, Sprite, Container, Text } from '@inlet/react-pixi';
import UnitEgg from './Unitegg'
import * as PIXI from 'pixi.js'
import UnitOpened from './UnitOpened'
import { items } from './UnitsMass';


export default function UnitsBlock() {
    const [index, setIndex] = useState(0)
    const [active, setActive] = useState(0)
    const [cur, setCur] = useState(1)

    let wWidth = window.innerWidth;
    window.addEventListener('resize', function (event) {
        wWidth = window.innerWidth;
    }, true);


    const style = new PIXI.TextStyle({
        align: "center",
        fontFamily: "Junegull",
        fontSize: 18,
        fontWeight: "bold",
        fill: ["#fff"],
    });

    const headerStyle = new PIXI.TextStyle({
        align: "center",
        fontFamily: "Junegull",
        fontSize: 42,
        fontWeight: "bold",
        fill: ["#7400BB"]
    });
    const descriptionStyle = new PIXI.TextStyle({
        fontFamily: "Golos",
        fontSize: 15,
        fontWeight: "400",
        fill: ["#7400BB"],
        lineHeight: 25,
        wordWrap: true,
        wordWrapWidth: wWidth > 780 ? 520 : 250
    });

    const showNext = () => {
        if (index + 1 > items.length - 3) {
            setIndex(0)
        } else {
            setIndex(index + 1)
        }

    }
    const showPrev = () => {
        if (index - 3 < 0) {
            setIndex(items.length - 3)
        } else {
            setIndex(index - 1)
        }
    }


    const startShowPrilipala = (id, id2) => {
        setCur(undefined)
        setActive(id);
        setCur(id2)
    }

    return (
       <></>
    )
}
