import React, { useState } from "react";
import InputMask from "react-input-mask";
import API from "../../utils/API";
import arr from '../../assets/img/btn-arr.png'
import { useDispatch } from 'react-redux';
import Countdown from 'react-countdown';




export default function LoginForm() {
    const [timer, setTimer] = useState(false)
    const [code, setCode] = useState()
    const [phoneNumber, setPhone] = useState()
    const dispatch = useDispatch();


    const Completionist = () => (
        <a className="btnLink orange"
            onClick={() => stopTimer()}
        >Отправить повторно</a>
    );
    const startTimer = (e) => {       

        handleSendCode(clearNumber.replace('+', ''))
        $('#user_phone').val('')
        $('.modalBox').trigger('click')
    }

    // Код отправлен повторно
    const  stopTimer = () => {
        this.setState({
            timer: 'ended'
        })
        const { handleSendCode } = this.props
        handleSendCode(this.state.number)
        $('.modalBox__container').trigger('click')
    }

    const getCode = (e) => {
        e.preventDefault()
        console.log(phoneNumber)
        API.post('/login_sms', {
            phone: phoneNumber
        }).then(res => {
            console.log(res.data)
            if (res.data.success) {
                startTimer()
            }
        })
    }
    const login = async () => {
        await API.post('/sms')
            .then(res => console.log(res.data))
    }
    const setCorrectPhone = (e) => {
        let cur = e.target.value
        cur = cur.replaceAll(/\s/g, '').replaceAll(/[{()}]/g, '').replaceAll('+', '')
        setPhone(cur)
    }

    return (
        <div>
            <h2 className='text-violet text-center text-3xl mb-8'>
                вход/регистрация </h2>
            <form
                onSubmit={
                    (e) => {
                        e.preventDefault();
                        !code ? getCode(e) : login(e)
                    }
                }
            >
                <InputMask
                    className='w-full'
                    placeholder={timer ? 'Введите код' : 'Введите номер телефона'}
                    /*{...this.state}*/
                    mask={timer ? '999999' : "+7 (999) 999 99 99"}
                    required
                    autofill='false'
                    id="user_phone"
                    value={timer ? code : phoneNumber}
                    onChange={(e) => { timer ? setCode(e.target.value) : setCorrectPhone(e) }}
                    maskChar="" />
                <Countdown date={Date.now() + 59000} intervalDelay={1000} precision={.3} renderer={renderer}>
                    <Completionist />
                </Countdown>
                <button className='btn grid py-4 px-8 w-fit mx-auto mt-8' type='submit'>
                    <span>отправить код</span>
                    <img src={arr} alt="" />
                </button>
            </form>
        </div >
    )
}
