import React, { useEffect, useRef, useState } from "react";
import { Sprite, Container, Text } from '@inlet/react-pixi';
import { gsap } from "gsap";
import { TweenLite } from "gsap/src";
import { TweenMax } from "gsap/gsap-core";
import { DropShadowFilter } from '@pixi/filter-drop-shadow';




export default function UnitOpened({ items, cur, headerStyle, ref }) {

    const [shake, shakeEgg] = useState(true)

    const prilipalaRef = useRef()
    const topEggRef = useRef()
    const bottomEggRef = useRef()
    const eggRef = useRef()


    const prilipalaShow = () => {
        TweenLite.to(prilipalaRef.current,
            1.5,
            {
                x: "170",
                y: "200",
                rotation: '-.3',
                height: 361.5,
                width: 259.5,
                ease: "power4.inOut",
                alpha: 1,
                delay: 2
            },


        );
    }

    useEffect(() => {
        eggRef.current.filters = [new DropShadowFilter({
            distance: 10,
            rotation: 24,
            blur: 5,
            color: '0x674EA7'
        })];
        prilipalaRef.current.filters = [new DropShadowFilter({
            distance: 10,
            rotation: 24,
            blur: 5,
            color: '0x674EA7'
        })];
        TweenMax.to(eggRef.current, .05,
            {
                x: "-=10", yoyo: true, repeat: 15
            }
        )

        TweenMax.to(topEggRef.current, .5, {
            y: 50,
            rotation: '.5',
            delay: 1.5,
        })
        TweenMax.to(bottomEggRef.current, .5, {
            y: 380,
            rotation: '-.5',
            delay: 1.5,
            css: {
                filter: 'drop-shadow(1px 1px 10px violet)'
            }
        })
        prilipalaShow()
    }, [])

    return (
        <Container x={370} y={40} width={600} height={580} >
            <Container ref={eggRef}>
                <Sprite image={items[cur - 1].topEgg} width={261} height={187} x={450} y={135} anchor={.5} ref={topEggRef} />
                <Sprite image={items[cur - 1].bottomEgg} width={260} height={178} x={450} y={300} anchor={.5} ref={bottomEggRef} />
            </Container>
            <Sprite image={items[cur - 1].prilipala} width={173} height={241} x={360} y={300} alpha={0} anchor={.5} ref={prilipalaRef} />
            <Text text={items[cur - 1].name} style={headerStyle} x={50} y={430} />
        </Container>
    )
}
