import { FormControl, InputLabel, MenuItem, Select, TextField } from "@material-ui/core";
import React, { useState } from "react";
import { REGION_LIST } from '../Map/REGION_LIST.js'
import { useForm } from 'react-hook-form'
import ReactInputMask from "react-input-mask";
import arr from '../../assets/img/btn-arr.png'



export default function UserForm() {
    const [values, setValues] = useState({
        first_name: "",
        phone: "",
        email: "",
        file: "",
        region: ""
    })
    const [file, setFile] = useState()
    const handleChange = (e) => {
        const key = e.target.id;
        const value = e.target.value
        setValues(values => ({
            ...values,
            [key]: value,
        }))
    }
    const setFileValue = (e) => {
        setFile(document.getElementById('file').files[0])
        console.log(document.getElementById('file').files[0])
    }

    const { register, handleSubmit, formState: { errors } } = useForm();
    const onSubmit = async e => {
        console.log(e)
    };

    return (
        <div className="row justify-content-center">
            <header >
                <div className="row justify-content-center">
                    <div className="col-lg-auto">
                        <h2 className='text-violet text-5xl mb-4'>
                            регистрация </h2>
                        <p className='text-violet'>Принять участие в розыгрыше могут только лица, достигшие 18 лет</p>
                    </div>
                </div>

            </header>
            <form
                className='mt-8'
                onSubmit={handleSubmit(onSubmit)}
            >
                <div className="grid grid-cols-2 gap-4">
                    <div className="w-full">
                        <TextField
                            className="w-full"
                            id="first_name"
                            name="first_name"
                            label="ФИО"
                            variant="outlined"
                            error={errors.first_name}
                            value={values.first_name}
                            helperText={errors.first_name && errors.first_name.message}
                            {...register("first_name", {
                                required: true,
                                pattern: {
                                    value: /[А-ЯЁA-Z]{2,15}/i,
                                    message: 'Только буквы и пробел'
                                }
                            })}
                            onChange={handleChange}
                        />

                    </div>
                    <div className="w-full">

                        <ReactInputMask
                            className='w-full'
                            placeholder={'Введите номер телефона'}
                            mask={"+7 (999) 999-99-99"}
                            required
                            autofill='false'
                            id="user_phone"
                            error={errors.phone}
                            value={values.phone}
                            helperText={errors.phone && errors.phone.message}
                            {...register("phone", {
                                required: 'Обязательно',
                                pattern: {
                                    value: /[А-ЯЁA-Z]{2,15}/i,
                                    message: 'Только буквы и пробел'
                                }
                            })}
                            onChange={handleChange}
                            maskChar="" />
                    </div>
                    <div className="w-full">
                        <TextField
                            label="E-mail"
                            id="email"
                            value={values.email}
                            variant="outlined"
                            error={errors.email}
                            helperText={errors.email && errors.email.message}
                            {...register("email", {
                                required: 'Обязательно',
                                pattern: {
                                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                                    message: 'Неправильный адрес электронной почты'
                                }
                            })}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="w-full">
                        <TextField
                            label="Возраст"
                            id="age"
                            type="number"
                            className=" w-full "
                            value={values.age}
                            variant="outlined"
                            error={errors.age}
                            pattern={/[0-9]{3}/i}
                            helperText={errors.age && errors.age.message}
                            {...register("age", {
                                required: 'Обязательно',
                                pattern: {
                                    value: /[0-9]{3}/i,
                                    message: 'Введите 2 цифры'
                                }
                            })}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="w-full">
                        <FormControl variant="outlined">
                            <InputLabel htmlFor="outlined-age-native-simple">
                                Выберите регион
                            </InputLabel>
                            <Select
                                labelId="demo-customized-select-label"
                                id="demo-customized-select"
                                value={values.region}
                                onChange={handleChange}
                                label="Выберите регион"
                                inputProps={{
                                    name: "Выберите регион",
                                    id: "outlined-age-native-simple",
                                }}
                            >
                                {REGION_LIST.map((item, index) => (
                                    <MenuItem value={item.city} key={index}>{item.city}</MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    </div>
                    <div className="w-full file relative">
                        <input
                            required
                            type="file"
                            className=" w-full "
                            name="file"
                            id="file"
                            onChange={setFileValue}
                        />
                        <label htmlFor="file" className='w-full'>{file ? file.name : `Загрузить фото коробки с яйцами`}
                            <div className="btn lil-btn">+</div>
                        </label>
                    </div>

                </div>
                {/* {!confirmedRules && */}
                <div className="row justify-content-center">
                    <div className="col-lg-auto">
                        <div className="row politics">
                            <input
                                type="checkbox"
                                name="user_accept"
                                id="user_accept"
                                required
                            />
                            <label htmlFor="user_accept" className="check-box"></label>
                            <p>Я согласен с{' '}
                                <a
                                    className="btnLink orange"
                                    href="https://dixy.ru/politika-obrabotki-i-zashity.pdf"
                                    target="_blank">Политикой Конфиденциальности и обработки моих данных</a>, ознакомлен с правилами акции.
                            </p>
                        </div>
                    </div>
                </div>
                {/* } */}
                {/* {systemMessage && <div className="col-lg-auto systemMessage mt-4 text-center">{systemMessage}</div>} */}
                <div className="mx-auto" style={{ width: 'fit-content', display: 'grid' }}>
                    <button className='btn grid mt-8 py-4 px-8'>
                        <span>сохранить</span>
                        <img src={arr} alt="" />
                    </button>

                </div>
            </form>
        </div>
    )

}
