import { Checkbox, FormControl, FormControlLabel, InputLabel, MenuItem, Select, TextField } from "@material-ui/core";
import React, { useState } from "react";
import { REGION_LIST } from '../Map/REGION_LIST.js'
import { useForm } from 'react-hook-form'
import arr from '../../assets/img/btn-arr.png'
import API from "../../utils/API.js";
// import DesktopDatePicker from '@mui/lab/DesktopDatePicker';



export default function UserForm({ close }) {
    const [values, setValues] = useState({
        first_name: "",
        email: "",
        file: "",
        region: "",
        agree: "",
        date: ""
    })
    const [file, setFile] = useState()
    const handleChange = (e) => {
        const key = e.target.id;
        const value = e.target.value
        setValues(values => ({
            ...values,
            [key]: value,
        }))
    }

    const handleChangeRegion = e => {
        setValues(values => ({
            region: e.target.value
        }))
    }

    const setFileValue = (e) => {
        setFile(document.getElementById('file').files[0])
    }

    const { register, handleSubmit, formState: { errors } } = useForm();
    const onSubmit = async data => {
        let formData = new FormData();
        data.filter(
            (item,id)=>{
                Form.data.append(item)
            }
        )
        formData.append('file', file)

        data.file = file
        API.post('/eggs_photo', formData)
            .then(
                res => {
                    console.log(res.data)
                }
            )
    };

    return (
        <div className="row justify-content-center">
            <header >
                <div className="row justify-content-center">
                    <div className="col-lg-auto">
                        <h2 className='text-violet text-5xl mb-4'>
                            регистрация
                        </h2>
                        <p className='text-violet'>Принять участие в розыгрыше могут только лица, достигшие 18 лет</p>
                    </div>
                </div>

            </header>
            <form
                className='mt-8'
                onSubmit={handleSubmit(onSubmit)}
            >
                <div className="grid lg:grid-cols-2 gap-4">
                    <div className="w-full">
                        <TextField
                            className="w-full"
                            id="first_name"
                            name="first_name"
                            label="Имя"
                            variant="outlined"
                            error={errors.first_name}
                            value={values.first_name}
                            helperText={errors.first_name && errors.first_name.message}
                            {...register("first_name", {
                                required: 'Обязательно',
                                pattern: {
                                    value: /[А-ЯЁA-Z]{2,15}/i,
                                    message: 'Только буквы и пробел'
                                }
                            })}
                            onChange={handleChange}
                        />

                    </div>
                    <div className="w-full">
                        <TextField
                            label="E-mail"
                            id="email"
                            value={values.email}
                            variant="outlined"
                            error={errors.email}
                            helperText={errors.email && errors.email.message}
                            {...register("email", {
                                required: 'Обязательно',
                                pattern: {
                                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                                    message: 'Неправильный адрес электронной почты'
                                }
                            })}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="w-full">
                        <TextField
                            id="date"
                            variant="outlined"
                            label="Дата рождения"
                            type="date"
                            InputLabelProps={{
                                shrink: true,
                            }}
                            error={errors.date}
                            helperText={errors.date && errors.date.message}
                            {...register("date", {
                                required: 'Обязательно',
                            })}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="w-full">
                        <FormControl variant="outlined">
                            <InputLabel htmlFor="outlined-age-native-simple">
                                Выберите регион
                            </InputLabel>
                            <Select
                                labelId="demo-customized-select-label"
                                id="region"
                                value={values.region}
                                onChange={handleChangeRegion}
                                label="Выберите регион"
                                inputProps={{
                                    name: "Выберите регион",
                                    id: "outlined-age-native-simple",
                                }}
                            >
                                {REGION_LIST.map((item, index) => (
                                    <MenuItem value={item.city} key={index}>{item.city}</MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    </div>
                    <div className="w-full file relative">
                        <input
                            type="file"
                            className=" w-full "
                            name="file"
                            id="file"
                            onChange={setFileValue}
                        />
                        <label htmlFor="file" className='w-full'>{file ? file.name : `Загрузить фото коробки с яйцами`}
                            <div className="btn lil-btn">+</div>
                        </label>
                    </div>
                    <div className="w-full ">
                        <FormControlLabel
                            className='w-full check-box'
                            style={{ color: '#520084' }}
                            control={<Checkbox />}
                            id="agree"
                            {...register("agree", {
                                required: 'Обязательно',
                            })}
                            label={<p>Я согласен с <a className='underline hover:no-underline' href='https://dixy.ru/politika-obrabotki-i-zashity.pdf' target='_blank'>условиями обработки персональных данных</a></p>}
                        />
                    </div>
                </div>

                <div className="mx-auto" style={{ width: 'fit-content', display: 'grid' }}>
                    <button className='btn grid mt-8 py-4 px-8' type='submit'>
                        <span>сохранить</span>
                        <img src={arr} alt="" />
                    </button>

                </div>
            </form>
        </div>
    )

}
