import React, { useEffect, useRef, useState } from "react";
import { Sprite, Container, Text } from '@inlet/react-pixi';
import bottomBg from '../../assets/img/units/zemlyaka/bottom-bg.png'
import { TweenMax } from "gsap/gsap-core";
import { DropShadowFilter } from '@pixi/filter-drop-shadow';




export default function UnitOpened({ items, cur, headerStyle, descriptionStyle, wWidth }) {
    const prilipalaRef = useRef()
    const topEggRef = useRef()
    const bottomEggRef = useRef()
    const eggRef = useRef()


    const prilipalaShow = () => {
        TweenMax.to(eggRef.current, .05,
            {
                x: "-=10", yoyo: true, repeat: 15
            }
        )

        TweenMax.to(topEggRef.current, .35, {
            y: 30,
            rotation: '.4',
            delay: .75,
        })
        TweenMax.to(bottomEggRef.current, .35, {
            y: 270,
            rotation: '-.4',
            delay: .75,
            css: {
                filter: 'drop-shadow(1px 1px 10px violet)'
            }
        })
        TweenMax.to(prilipalaRef.current,
            .75,
            {
                x: "150",
                y: "150",
                rotation: '-.3',
                height: items[cur - 1].p_width[1] * .6,
                width: items[cur - 1].p_width[0] * .6,
                ease: "power4.inOut",
                alpha: 1,
                delay: .8
            },
        );
    }

    // useEffect(() => {
    //     eggRef.current.filters = [new DropShadowFilter({
    //         distance: 10,
    //         rotation: 24,
    //         blur: 5,
    //         color: '0x674EA7'
    //     })];
    //     prilipalaRef.current.filters = [new DropShadowFilter({
    //         distance: 10,
    //         rotation: 24,
    //         blur: 5,
    //         color: '0x674EA7'
    //     })];

    //     prilipalaShow()
    // }, [])

    return (
        <Container x={wWidth > 780 ? 370 : 0} y={40} width={600} height={580} >
            <Sprite image={items[cur - 1].prilipala} width={items[cur - 1].p_width[0] * .4} height={items[cur - 1].p_width[1] * .4} x={430} y={137} anchor={.5} ref={prilipalaRef} />
            <Container ref={eggRef}>
                <Container ref={bottomEggRef} x={wWidth > 780 ? 450 : 200} y={wWidth > 780 ? 217 : 150}>
                    <Sprite image={items[cur - 1].bottomEgg} width={wWidth > 780 ? 260 * .8 : 260 * .5} height={wWidth > 780 ? 178 * .8 : 178 * .5} anchor={.5} />
                </Container>
                <Sprite
                    image={items[cur - 1].topEgg}
                    width={wWidth > 780 ? 261 * .8 : 261 * .5}
                    height={wWidth > 780 ? 187 * .8 : 187 * .5}
                    x={450}
                    y={85}
                    anchor={.5} ref={topEggRef} />
            </Container>

            <Text text={items[cur - 1].name} style={headerStyle} x={50} y={wWidth > 780 ? 290 : 200} />
            <Text text={items[cur - 1].description} x={50} y={wWidth > 780 ? 350 : 250} style={descriptionStyle} />

        </Container>
    )
}
