import { FormControl, InputLabel, MenuItem, Select, TextField } from "@material-ui/core";
import React, { useState } from "react";
import { REGION_LIST } from '../Map/REGION_LIST.js'
import { useForm } from 'react-hook-form'



export default function UserForm() {
    const [values, setValues] = useState({
        first_name: "",
        last_name: "",
        email: ""
    })
    const handleChange = (e) => {
        const key = e.target.id;
        const value = e.target.value
        setValues(values => ({
            ...values,
            [key]: value,
        }))
    }


    const { register, handleSubmit, formState: { errors } } = useForm({
        reValidateMode: 'onBlur',
        shouldUseNativeValidation: false
    });
    console.log(useForm);
    const onSubmit = async e => {
        e.preventDefault();

    };

    return (
        <div className="row justify-content-center">
            <header >
                <div className="row justify-content-center">
                    <div className="col-lg-auto">
                        <h2 className='text-violet text-5xl mb-4'>
                            регистрация </h2>
                        <p className='text-violet'>Принять участие в розыгрыше могут только лица, достигшие 18 лет</p>
                    </div>
                </div>
                <form
                    className='mt-4'
                    onSubmit={handleSubmit(onSubmit)}
                >
                    <div className="grid grid-cols-2 gap-4">
                        <div className="w-full">
                            <TextField
                                className="w-full "
                                id="first_name"
                                variant="outlined"
                                error={errors.first_name}
                                helperText={errors.first_name && errors.first_name.message}
                                {...register("first_name", {
                                    required: true,
                                    pattern: {
                                        value: /[А-ЯЁA-Z]{2,15}/i,
                                        message: 'Только буквы и пробел'
                                    }
                                })}
                                onChange={handleChange}
                            />
                        </div>
                        <div className="w-full">
                            <input
                                // required
                                type="text"
                                className=" w-full "
                                name="user_" id="user_"
                                placeholder="Фамилия"
                            // value={lastName && lastName}
                            // onChange={(e) => { this.changeData(e, 'lastName') }}
                            />
                        </div>
                        <div className="w-full">
                            <input
                                // required
                                type="email"
                                className=" w-full "
                                name="user_" id="user_"
                                placeholder="E-mail"
                            // value={email && email}
                            // onChange={(e) => { this.changeData(e, 'email') }}
                            />
                        </div>
                        <div className="w-full">
                            <input
                                // required
                                type="number"
                                className=" w-full "
                                name="user_age" id="user_age"
                                placeholder="Возраст"
                            // value={birth && birth}
                            // onChange={(e) => { this.changeData(e, 'birth') }}
                            />
                        </div>
                        <div className="w-full">
                            {/* <FormControl variant="outlined">
                                <InputLabel htmlFor="outlined-age-native-simple">
                                    Выберите регион
                                </InputLabel>
                                <Select
                                    labelId="demo-customized-select-label"
                                    id="demo-customized-select"
                                    // value={region}
                                    // onChange={(e) => { this.changeData(e, 'region') }}
                                    label="Выберите регион"
                                    inputProps={{
                                        name: "Выберите регион  ",
                                        id: "outlined-age-native-simple",
                                    }}
                                >
                                    {REGION_LIST.map((item, index) => (
                                        <MenuItem value={item.city} key={index}>{item.city}</MenuItem>
                                    ))}
                                </Select>
                            </FormControl> */}
                        </div>
                        <div className="w-full file relative">
                            <input
                                // required
                                type="file"
                                className=" w-full "
                                name="user_file" id="user_file"
                                placeholder="Возраст"
                            // value={birth && birth}
                            // onChange={(e) => { this.changeData(e, 'birth') }}
                            />
                            <label htmlFor="user_file" className='w-full'>Загрузить фото коробки с яйцами
                                <div className="btn lil-btn">+</div>
                            </label>
                        </div>

                    </div>
                    {/* {!confirmedRules && */}
                    <div className="row justify-content-center">
                        <div className="col-lg-auto">
                            <div className="row politics">
                                <input
                                    type="checkbox"
                                    name="user_accept"
                                    id="user_accept"
                                    required
                                />
                                <label htmlFor="user_accept" className="check-box"></label>
                                <p>Я согласен с{' '}
                                    <a
                                        className="btnLink orange"
                                        href="https://dixy.ru/politika-obrabotki-i-zashity.pdf"
                                        target="_blank">Политикой Конфиденциальности и обработки моих данных</a>, ознакомлен с правилами акции.
                                </p>
                            </div>
                        </div>
                    </div>
                    {/* } */}
                    {/* {systemMessage && <div className="col-lg-auto systemMessage mt-4 text-center">{systemMessage}</div>} */}
                    <div className="mx-auto" style={{ width: 'fit-content', display: 'grid' }}>
                        <button className='btn grid mt-8 py-4 px-8'>
                            <span>сохранить</span>
                        </button>

                    </div>
                </form>
            </header>
        </div>
    )

}
