import React, { useEffect, useRef } from "react";
import { Sprite, Container, Text } from '@inlet/react-pixi';
import { gsap } from "gsap";
import { TweenLite } from "gsap/src";
import { TweenMax } from "gsap/gsap-core";


function useGsapEffect(target, effect, vars) {
    const [animation, setAnimation] = useState();

    useLayoutEffect(() => {
      setAnimation(gsap.effects[effect](target.current, vars));    
    }, [effect]);

    return animation;
  }

export default function UnitOpened({ items, cur, headerStyle, ref }) {

    const prilipalaRef = useRef()
    const topEggRef = useRef()
    const bottomEggRef = useRef()
    const eggRef = useRef()

    const prilipalaShow = () => {
        TweenLite.to(prilipalaRef.current,
            1.5,
            {
                x: "170",
                rotation: '125.3',
                height: 361.5,
                width: 259.5,
                ease: "power4.inOut",
                alpha: 1
            },


        );
    }

    useEffect(() => {
        let animation = useGsapEffect(eggRef, "spin");
        TweenLite.to(eggRef.current, 1,
            {

            }
        )
    }, [])

    return (
        <Container x={370} y={20} width={600} height={530}>
            <Container ref={eggRef}>
                <Sprite image={items[cur - 1].topEgg} width={261} height={187} x={450} y={135} anchor={.5} ref={topEggRef} />
                <Sprite image={items[cur - 1].bottomEgg} width={260} height={178} x={450} y={300} anchor={.5} ref={bottomEggRef} />
            </Container>
            <Sprite image={items[cur - 1].prilipala} width={173} height={241} x={360} y={200} alpha={0} anchor={.5} ref={prilipalaRef} />
            <Text text={items[cur - 1].name} style={headerStyle} x={50} y={450} />
        </Container>
    )
}
