import React, { useState } from "react";
import InputMask from "react-input-mask";
import API from "../../utils/API";
import arr from '../../assets/img/btn-arr.png'



export default function LoginForm() {
    const [timer, startTimer] = useState(false)
    const [code, setCode] = useState()
    const [phoneNumber, setPhone] = useState()
    const getCode = async (e) => {
        e.preventDefault()
        await API.post('/login_sms', {
            phone: phoneNumber
        })
            .then(res => {
                console.log(res.data)
            })
    }
    const login = async () => {
        await API.post('/sms')
            .then(res => console.log(res.data))
    }

    return (
        <div>
            <h2 className='text-violet text-center text-3xl mb-8'>
                вход/регистрация </h2>
            <form
                onSubmit={
                    (e) => { e.preventDefault(); code ? getCode(e) : login(e) }
                }
            >
                <InputMask
                    className='w-full'
                    placeholder={timer ? 'Введите код' : 'Введите номер телефона'}
                    /*{...this.state}*/
                    mask={timer ? '999999' : "+7 (999) 999 99 99"}
                    required
                    autofill='false'
                    id="user_phone"
                    value={timer ? code : phoneNumber}
                    onChange={() => { timer ? setCode(code) : setPhone(phoneNumber) }}
                    maskChar="" />
                <button className='btn grid py-4 px-8 w-fit mx-auto mt-8' type='submit'>
                    <span>отправить код</span>
                    <img src={arr} alt="" />
                </button>
            </form>
        </div >
    )
}
