import React, { useState } from "react";
import Countdown from 'react-countdown';
import InputMask from 'react-input-mask';
import arr from '../../assets/img/btn-arr.png'



export default function LoginForm() {
    const [number, changeNumber] = useState('')
    const [requestMessage, setRequestMessage] = useState()
    const [timer, startTimer] = useState(false)
    const [verifyCodeError, setVerifyCodeError] = useState()
    const [code, setCode] = useState()

    return (
        <>
            <header>
                <div className="row justify-content-center">
                    <div className="col-lg-auto">
                        <h2 className='text-violet text-center text-3xl mb-4'>
                            вход/регистрация </h2>
                    </div>
                </div>
                <div className="row justify-content-center">
                    <div className="col-lg-12">
                        {number && !requestMessage ?
                            <div className="changing-data">
                                <p>Мы отправили код подтверждения на номер <span className="text-violet">{number}</span>.
                                    <p
                                        className="systemMessage forPhone cursor-pointer"
                                        onClick={changeNumber}
                                    >(Изменить номер)</p></p>
                            </div>
                            : <p>Введите номер телефона для входа на сайт. Мы вышлем код подтверждения в смс.</p>}
                    </div>
                </div>
            </header>
            <div className="modalBox__container__content_main">
                <form
                    autoСomplete="off"
                    // onSubmit={(e) => {
                    //     e.preventDefault()
                    //     !timer && startTimer(e)
                    // }}
                >
                    <input type="hidden" name="" autoComplete="false" />
                    {verifyCodeError &&
                        <div className="row">
                            <div className="col-lg-12">
                                <p className="errorMessage">{verifyCodeError}</p>
                            </div>
                        </div>}
                    <div className="row">
                        <div className="col-lg-12">
                            <InputMask
                                className='w-full'
                                placeholder={timer ? 'Введите код' : 'Введите номер телефона'}
                                /*{...this.state}*/
                                mask={timer ? '999999' : "+7 (999) 999 99 99"}
                                required
                                autofill='false'
                                id="user_phone"
                                value={timer ? code : number}
                                onChange={timer ? setCode : changeNumber}
                                maskChar="" />
                            {requestMessage && <p className="errorMessage">{requestMessage}</p>}
                        </div>
                    </div>
                    <div className="row justify-content-center">

                        {timer ?
                            <>
                                {!requestMessage && <div className="col-lg-12 systemMessage mt-4">
                                    <Countdown date={Date.now() + 59000} intervalDelay={1000} precision={.3} renderer={renderer}>
                                        <Completionist />
                                    </Countdown>
                                </div>}
                                <div className="col-lg-auto">
                                    <button className='btn grid mt-8 py-4 px-8'
                                        onClick={() => this.submitCode()}
                                    >
                                        <span>Подтвердить</span>
                                    </button>
                                </div>

                                <p className='systemMessage mt-4'>Нажимая кнопку «Подтвердить» я принимаю условия акции</p>

                            </>
                            :
                            <div className="col-lg-auto mt-8">
                                <div className="flex justify-center handleScanContainer__inner--input pb-4">
                                    <button className='btn grid py-4 px-8 w-fit' type='submit'>
                                        <span>отправить код</span>
                                        <img src={arr} alt="" />

                                    </button>
                                </div>
                            </div>
                        }
                    </div>

                </form>
            </div>
        </>
    )
}
