import React, { useEffect, useState } from "react";
import UnitsBlock from "./elements/UnitsBlock";
import FontFaceObserver from 'fontfaceobserver'


export default function Units() {

    const [font, loadFont] = useState(false)
    useEffect(() => {
        let font = new FontFaceObserver('Junegull');
        let fontB = new FontFaceObserver('Family B');

        font.load().then(function () {
            loadFont(true)
        });
    }, [])
    return (
        <section className='section units pt-16 -mb-32' id='heroes'>
            <div className="container after mx-auto">
                <h2 className="text-yellow text-center text-3xl xl:text-4xl 2xl:text-5xl mb-8">
                    Герои
                </h2>
                <div className="slider">
                    {font ? <UnitsBlock /> : null}
                </div>
            </div>
            <div className="container mx-auto mt-16">
                <div className="mystery-content">
                    <div className="grid grid-cols-2">
                        <div className="left catalog"></div>
                        <div className="right">
                            <h2 className="text-yellow text-3xl xl:text-4xl 2xl:text-5xl mb-8">
                            журнал
                            </h2>
                            <p className="mt-4 text text-white md:text-xl 2xl:text-2xl">
                                <div className="font-black">Собирай полную коллекцию стикеров в журнал,</div> а также играй в игры и узнавай интересные факты о нашей большой вселенной
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}
