import React, { useState } from "react";
import InputMask from "react-input-mask";
import API from "../../utils/API";



export default function LoginForm() {
    const [timer, startTimer] = useState(false)
    const [code, setCode] = useState()
    const [phoneNumber, setPhone] = useState('')
    const login = async () => {
        await API.post('/login_sms')
            .then(res => {
                console.log(res.data)
            })
    }

    return (
        <div>
            <h2 className='text-violet text-center text-3xl mb-4'>
                вход/регистрация </h2>
            <form>
                <InputMask
                    className='w-full'
                    placeholder={timer ? 'Введите код' : 'Введите номер телефона'}
                    /*{...this.state}*/
                    mask={timer ? '999999' : "+7 (999) 999 99 99"}
                    required
                    autofill='false'
                    id="user_phone"
                    value={timer ? code : phoneNumber}
                    onChange={timer ? setCode : setPhone}
                    maskChar="" />
            </form>
        </div>
    )
}
