import React, { useEffect, useState } from "react";
import IndexPage from "./IndexPage";
import "./assets/scss/main.scss";
// import NavJS from "./components/NavJS";
// import FooterComponent from "./components/FooterComponent";
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import API from "./utils/API";
import Fancybox from "./components/elements/Fancybox";
import arr from './assets/img/btn-arr.png'
import poster from './assets/img/poster.jpg'


export default function App() {
  const [video, setVideo] = useState()
  useEffect(() => {
    let str = window.location.search
    if (str.length) {
      API.get(`/prilipal/video${str}`)
        .then(
          res => {
            console.log(res.data.data)
            if (res.data.data) {
              setVideo(res.data.data.video)
            }
          }
        )
    }
  }, [])
  // console.log(video)
  return (
    <Router>
      <div className="app w-full ubuntu">
        {video ?
          <div className="modalBox">
            <Fancybox>
              <div className="box " style={{maxWidth: '300px'}}>
                <div className="box-container px-4">
                  <div className="box-image"> <img src={poster} alt="" /></div>
                </div>
              </div>

              <button
                data-fancybox="gallery"
                data-src={video}
                poster={poster}
                className="btn grid py-4 px-8 w-fit mx-auto mt-8 relative z-40"
              >
                Смотреть видео
                <img src={arr} alt="" />
              </button>
            </Fancybox>
          </div> : null}
        <Switch>
          <Route path='/tresnutye' exact><IndexPage page='main' /></Route>
          <Route path='/tresnutye/sponsors' exact><IndexPage page='sponsors' /></Route>
          <Route path='/tresnutye/map' exact><IndexPage page='map' /></Route>
        </Switch>
      </div>
    </Router >
  );
}
