import React, { useEffect, useRef, useState } from "react";
import { Sprite, Container, Text } from '@inlet/react-pixi';
import bottomBg from '../../assets/img/units/zemlyaka/bottom-bg.png'
import { TweenMax } from "gsap/gsap-core";
import { DropShadowFilter } from '@pixi/filter-drop-shadow';




export default function UnitOpened({ items, cur, headerStyle }) {
    const prilipalaRef = useRef()
    const topEggRef = useRef()
    const bottomEggRef = useRef()
    const eggRef = useRef()


    const prilipalaShow = () => {
        TweenMax.to(eggRef.current, .05,
            {
                x: "-=10", yoyo: true, repeat: 15
            }
        )

        TweenMax.to(topEggRef.current, .35, {
            y: 50,
            rotation: '.5',
            delay: .75,
        })
        TweenMax.to(bottomEggRef.current, .35, {
            y: 380,
            rotation: '-.5',
            delay: .75,
            css: {
                filter: 'drop-shadow(1px 1px 10px violet)'
            }
        })
        TweenMax.to(prilipalaRef.current,
            .75,
            {
                x: "170",
                y: "200",
                rotation: '-.3',
                height: items[cur - 1].p_width[1] * .8,
                width: items[cur - 1].p_width[0] * .8,
                ease: "power4.inOut",
                alpha: 1,
                delay: .8
            },
        );
    }

    useEffect(() => {
        eggRef.current.filters = [new DropShadowFilter({
            distance: 10,
            rotation: 24,
            blur: 5,
            color: '0x674EA7'
        })];
        prilipalaRef.current.filters = [new DropShadowFilter({
            distance: 10,
            rotation: 24,
            blur: 5,
            color: '0x674EA7'
        })];

        prilipalaShow()
    }, [])

    return (
        <Container x={370} y={40} width={600} height={580} >
            <Sprite image={items[cur - 1].prilipala} width={items[cur - 1].p_width[0] * .5} height={items[cur - 1].p_width[1] * .5} x={430} y={187} anchor={.5} ref={prilipalaRef} />


            <Container ref={eggRef}>
                <Container ref={bottomEggRef} x={450} y={300}>
                    <Sprite image={items[cur - 1].bottomEgg} width={260} height={178} anchor={.5} />
                </Container>
                <Sprite image={items[cur - 1].topEgg} width={261} height={187} x={450} y={135} anchor={.5} ref={topEggRef} />
            </Container>

            <Text text={items[cur - 1].name} style={headerStyle} x={50} y={430} />
            +                <Text text={items[cur - 1].description} x={50} y={410} style={descriptionStyle} />

        </Container>
    )
}
