import React from "react";
import i1 from '../assets/img/prizes/1.png'
import i2 from '../assets/img/prizes/2.png'
import i3 from '../assets/img/prizes/3.png'
import i4 from '../assets/img/prizes/4.png'
import i5 from '../assets/img/prizes/5.png'

const prizesMass = [
    {
        name: 'LEGO «Saturn V. NASA Apollo»',
        img: i1
    },
    {
        name: 'LEGO «Космический шаттл NASA - Discovery»',
        img: i2
    },
    {
        name: 'iPhone 13',
        img: i3
    },
    {
        name: 'iPad mini',
        img: i4
    },
    {
        name: 'Худи',
        img: i5
    }
]


export default function Prizes() {
    return (
        <section className='prizes py-20 mt-20'>
            <div className="container mx-auto">
                <h2 className="text-yellow text-center text-5xl mb-8">
                    Призы
                </h2>
                <div className="mt-16 blocks flex">
                    {prizesMass.map(
                        (prize, id) => (
                            <div className={`prize-block bg-red px-8 grid place-content-center ${id !== prizesMass.length ? 'mr-8' : ''}`} key={id}>
                                <div className="content">
                                    <div className="img-container"><img className='-rotate-12 transform' src={prize.img} alt="" /></div>
                                    <div className="data-container">
                                        <h4 className="text-center text-white font-normal leading-loose	">{prize.name}</h4>
                                    </div>
                                </div>
                            </div>
                        )
                    )}
                </div>
            </div>
        </section>
    )
}
