    },
    {
        id: 5,
        tresnutyi: img4,
        prilipala: img3,
        topEgg: img1,
        bottomEgg: img2,
        name: 'Земляка5',
        description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua'
    },
    {
        id: 6,
        tresnutyi: img4,
        prilipala: img3,
        topEgg: img1,
        bottomEgg: img2,
        name: 'Земляка6',
        description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua'
    }
]
const style = new PIXI.TextStyle({
    align: "center",
    fontFamily: "Junegull",
    fontSize: 18,
    fontWeight: "bold",
    fill: ["#fff"],
});

const headerStyle = new PIXI.TextStyle({
    align: "center",
    fontFamily: "Junegull",
    fontSize: 42,
    fontWeight: "bold",
    fill: ["#7400BB"]
});


export default function UnitsBlock() {
    const [index, setIndex] = useState(0)
    const [active, setActive] = useState(0)
    const [cur, setCur] = useState(1)

    const showNext = () => {
        if (index + 1 > items.length - 3) {
            setIndex(0)
        } else {
            setIndex(index + 1)
        }

    }
    const showPrev = () => {
        if (index - 3 < 0) {
            setIndex(items.length - 3)
        } else {
            setIndex(index - 1)
        }
    }


    const startShowPrilipala = () => {
        setCur(undefined)
        setActive(id); 
        etCur(egg.id)
    }

    return (
        <Stage width={1100} height={700} options={{ backgroundAlpha: 0 }}>
            <Sprite width={942} height={629} image={bg} x={0} y={30} />
            <Sprite image={bg2} x={335} y={0} width={678} height={619} />
            <Container>
                {items.slice(index, index + 3).map(
                    (egg, id) => (
                        <UnitEgg
                            egg={egg}
                            key={id}
                            x={120}
                            y={id === 1 ? 276 : id === 2 ? 410 : 140}
                            setActive={()=>startShowPrilipala(id)}
                            active={id === active ? true : false}

                        />
                    )
                )}
            </Container>
            <Container x={80} y={500}>
                <Sprite image={arr_up}
                    interactive={true}
                    buttonMode={true}
                    pointerdown={showPrev}
                />
                <Sprite image={arr_down}
                    interactive={true}
                    buttonMode={true}
                    x={80}
                    pointerdown={showNext}
                />
                <Text text={`${cur} / ${items.length}`} style={style} x={160} y={30} />
            </Container>

            {cur !== undefined ?
                <UnitOpened
                    items={items}
                    cur={cur}
                    headerStyle={headerStyle}
                /> : null}

        </Stage>
    )
}
