import React, { useEffect } from "react";
import Slider from "react-slick";
import { series } from "../constants/series";


function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
        <button
            className='btn px-5 py-5 slick-btn-next'
            onClick={onClick}
        />
    );
}
function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
        <button
            className='btn px-5 py-5 slick-btn-prev'
            onClick={onClick}
        />
    );
}


export default function Serial() {
    const settings = {
        dots: false,
        // infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1,
        lazyLoad: true,
        nextArrow: <SampleNextArrow />,
        prevArrow: <SamplePrevArrow />,

        responsive: [
            {
                breakpoint: 1351,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 998,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                }
            }
        ]

    };
    return (
        <section className="serial py-16">
            <div className="container mx-auto">
                <h2 className="text-yellow text-center text-3xl xl:text-4xl 2xl:text-5xl">
                    мультсериал
                </h2>
                <Slider className='mt-16 pb-28' {...settings}>
                    {series.map(
                        (item, id) =>
                        (
                            <div className="box-container px-4" key={id}>
                                <Fancybox>
                                    <button
                                        data-fancybox="gallery"
                                        data-src={item.url}
                                        className="box"
                                    >
                                        <img src={item.poster} className='rounded-3xl box-image' alt="" />
                                        <p className='mt-2 text-white '>{item.name}</p>
                                    </button>
                                </Fancybox>
                            </div>)
                    )}

                </Slider>
            </div>
        </section>
    )
}
