import React, { useEffect, useState } from "react";
import logo from './assets/img/logo-soon.png'
import tres from './assets/img/tres.png'
import { Hoc } from "./Hoc";
// WinnersComponent

function IndexPage() {
  const [days, setDays] = useState(13)
  const [hours, setHours] = useState(8)
  const [minutes, setMinutes] = useState(45)

  function getTimeRemaining(endtime) {
    let t = Date.parse(endtime) - Date.parse(new Date());
    let seconds = Math.floor((t / 1000) % 60);
    let minutes = Math.floor((t / 1000 / 60) % 60);
    let hours = Math.floor((t / (1000 * 60 * 60)) % 24);
    let days = Math.floor(t / (1000 * 60 * 60 * 24));
    return {
      'total': t,
      'days': days,
      'hours': hours,
      'minutes': minutes,
      'seconds': seconds
    };
  }

  let deadline = 'November 22 2021';

  function initializeClock(id, endtime) {

    function updateClock() {
      let t = getTimeRemaining(endtime);

      setDays(t.days);
      setHours(('0' + t.hours).slice(-2));
      setMinutes(('0' + t.minutes).slice(-2));

      if (t.total <= 0) {
        clearInterval(timeinterval);
      }
    }

    updateClock();
    let timeinterval = setInterval(updateClock, 1000);
  }
  useEffect(() => {
    initializeClock('countdown', deadline);
  }, [])
  return (
    <div className="content soon">
      <div className="flex flex-col items-center py-8">
        <div className="logo ">
          <img src={logo} alt="" />
        </div>
        <div className="tresnutye max-w-2xl mb-8">
          <img src={tres} alt="" />
        </div>
        <div className="timer flex mb-8">
          <div className="days">
            <div className="box junegull">{days}</div>
            дней
          </div>
          <div className="hours">
            <div className="box junegull">{hours}</div>
            часов
          </div>
          <div className="mins">
            <div className="box junegull">{minutes}</div>
            минут
          </div>
        </div>
        <h4 className='junegull text-white text-2xl dateFull'>с 22 ноября</h4>
      </div>
    </div>
  );

}

export default Hoc(IndexPage);
