import React, { useState, useEffect } from "react";
import LoginForm from "./LoginForm";
import UserForm from "./UserForm";
import UserAccount from "./UserAccount";
import API from "../../utils/API.js";




export default function FormComponent({ setForm, form, setToken }) {
  const [userData, setUserData] = useState(false)
  const loginUser = () => {
    API.get('/profile')
      .then(
        res => {
          if (res.data.data) {
            setUserData(res.data.data)
          }
        }
      )
  }
  useEffect(() => {
    if (token) {
      loginUser()
    }
  }, [token])


  const close = () => {
    document.querySelector("#root").classList.remove("scrolled");
    setForm(false)
  }
  return (
    <div className={form ? "modalBox " : "modalBox hidden"}>
      <div className={`modalBox__container ${!token ? 'phone' : ''}`}>
        <div className="close-btn__container cursor-pointer relative z-30" onClick={close}>
          <div className="close-btn">
            <span></span>
            <span></span>
          </div>
        </div>
        <div className="modalBox__container__content">
          {token ?
            <>
              {!userData ? <UserForm setForm={setForm} form={form} /> : <UserAccount />}
            </>
            :
            <LoginForm setToken={setToken} />}
        </div>
      </div>
    </div>
  )
}
